(function() {
	var a = $("#content");
	if (theme_options.infinite_scroll == true) {
		$("#pagination").hide();
		a.infinitescroll({
			navSelector: "nav#pagination",
			nextSelector: "nav#pagination a:first",
			itemSelector: ".load-more",
			bufferPx: 100,
			done: "",
			loading: {
				img: "http://static.tumblr.com/wgijwsy/YJ1m2tzmw/loading.gif",
				finishedMsg: "All posts have been loaded"
			}
		}, function(d) {
			var b = [],
				c = $(d),
				e = c.css({
					opacity: 0
				});
			c.each(function() {
				b.push(this.id)
			});
			e.imagesLoaded(function() {
				e.animate({
					opacity: 1
				}, "500");
				a.masonry("appended", c);
				$(".photoset-grid").photosetGrid({
					highresLinks: true,
					gutter: "5px",
					onComplete: function() {
						a.masonry()
					}
				})
			});
			Tumblr.LikeButton.get_status_by_post_ids(b)
		})
	}
	/*
	a.imagesLoaded(function() {
		a.masonry();
		$(".photoset-grid").photosetGrid({
			highresLinks: true,
			gutter: "5px",
			onComplete: function() {
				a.masonry()
			}
		})
	})*/
})();